data = '''



'''

instructions = data.splitlines()

BUTTONS = ('123', '456', '789')
BUTTONS2 = (('', '', '1', '', ''), ('', '2', '3', '4', ''), '56789', ('', 'A', 'B', 'C', ''), ('', '', 'D', '', ''))
DIRECTIONS = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}

def coordadd(coord1, coord2):
    row, col = (coord1[0] + coord2[0], coord1[1] + coord2[1])
    #if not (row in range(3) and col in range(3)):
    if not (row in range(5) and col in range(5)) or BUTTONS2[row][col] == '':
        return coord1
    return (row, col)

position = (1, 1)
code = ''
for instruction in instructions:
    for step in instruction:
        position = coordadd(position, DIRECTIONS[step])
    code += BUTTONS2[position[0]][position[1]]
print(code)
